unitsize(0.04cm);
defaultpen(linewidth(0.7));
pair a=(100,0), b=(100^2,0), c=(100,100^2-100), d=(100^2+100,100^2-100), e=(0,100^2);
draw(a--b--d--e--a);
label("$x$",(50,0),S);
